import * as PIXI from "pixi.js";
import * as CONST from "./const.js";
import { TweenMax, TimelineLite, Power2, Elastic, CSSPlugin, TweenLite, TimelineMax } from "gsap/TweenMax";
const ASSETS = CONST.ASSETS

export const init = (app, setup) => {

  // Constants
  const BLUE_TEXTURE = new PIXI.Texture.from(CONST.ASSETS.BLUE_CIRCLE)
  const LINE_PERCENTAGE = 0.8
  const PIN_TEXTURE = new PIXI.Texture.from(ASSETS.SHARP_PIN)
  const MEASURE_PIN_TEXTURE = new PIXI.Texture.from(ASSETS.MEASURE_PIN)


 // Initial State
  let state = {
    valA: 8,
    valB: 8,
    lineMax: 30,
  }

  console.log("setupwidth",setup.width)
  console.log("windowheight",window.innerHeight)

  // Layout Parameters
  let WINDOW_WIDTH = setup.width
  let WINDOW_HEIGHT = setup.height
  let BAR_HEIGHT = WINDOW_HEIGHT/15
  let LINE_WIDTH = WINDOW_WIDTH*0.8

  class Row extends PIXI.Container {

    constructor(num,den,width,ID) {
      super()
        // Default values
      this.numerator = num
      this.denominator = den
      this.width = width
      this.id = ID
      this.sprites = []
      this.blockWidth = width / this.denominator


      // Init Graphics A
      this.graphicsA = new PIXI.Graphics()
      this.graphicsA.lineStyle(3,0x000000) 
      this.graphicsA.drawRoundedRect(0,0,this.blockWidth,BAR_HEIGHT,1)
      this.textureA = app.renderer.generateTexture(this.blockA)

      // Init Graphics B
      this.graphicsB  = new PIXI.Graphics()
      this.graphicsB.beginFill(0xff4772)
      this.graphicsB.lineStyle(3,0x000000) 
      this.graphicsB.drawRoundedRect(0,0,this.blockWidth,BAR_HEIGHT,1)
      this.textureB = app.renderer.generateTexture(this.blockB)

      // Dummy frame for when animating between different fractional parts.
      this.frameGraphics = new PIXI.Graphics()    

      // Generate Row
      for (let i = 0;i<this.denominator;i++) {
        console.log("balls")
        let s = new PIXI.Sprite.from(this.textureA)
        s.on('pointerdown',this.spritePointerDown)
        s.on('pointerup',this.spritePointerUp)
        s.on('pointermove',this.spritePointerMoved)
        s.interactive = true
        s.active = false
        s.x = i*LINE_WIDTH/this.denominator
        s.y = WINDOW_HEIGHT/2 - BAR_HEIGHT
        this.sprites.push(s)
        this.addChild(s)
      }
            //  Attached methods
      this.on('pointerdown',this.pointerDown)
      this.on('pointerup',this.pointerUp)
      this.on('pointermove',this.pointerMove)

    }


    incDenonimator = (inc) => {
      this.frameGraphics.clear()
      this.frameGraphics.lineStyle(3,0x000000) 
      this.frameGraphics.drawRoundedRect(0,0,this.width,BAR_HEIGHT,1)
      let R = app.renderer.generateTexture(this.frameGraphics)
      let s = new PIXI.Sprite()
      this.container.addChild(s)
      s.texture = R
      s.x  = 0

      if (inc > 0){
        const onComplete = ()=>{
          s.on('pointerdown',this.spritePointerDown)
          s.on('pointerup',this.spritePointerUp)
          s.on('pointermove',this.spritePointerMoved)
          s.interactive = true
          s.active = false
          this.sprites.push(s)
          this.draw()
        }
        TweenMax.to(this, 0.25, {denominator: this.denominator+1,onUpdate: this.draw,onComplete: onComplete})
      } else if (inc < 0) {
        let removeme  = this.sprites.pop()
        this.container.removeChild(removeme)
        const onComplete = ()=>{
          this.draw()
          this.container.removeChild(s)
        }
        TweenMax.to(this, 0.25, {denominator: this.denominator-1,onUpdate: this.draw,onComplete: onComplete})
      }
    }

    draw(width) {

      if (width) {
        this.width = width
      }

      this.blockWidth = this.width/this.denominator

      this.blockA.clear()
      this.blockA.lineStyle(3,0x000000) 
      this.blockA.drawRoundedRect(0,0,this.blockWidth,BAR_HEIGHT,1)
      this.textureA = app.renderer.generateTexture(this.blockA)

      this.blockB.clear()
      this.blockB.beginFill(0xff4772)
      this.blockB.lineStyle(3,0x000000) 
      this.blockB.drawRoundedRect(0,0,this.blockWidth,BAR_HEIGHT,1)
      this.textureB = app.renderer.generateTexture(this.blockB)

      for (let i = 0;i<this.sprites.length;i++){
        if (this.sprites[i].active){
          this.sprites[i].texture = this.textureA
        } else {
          this.sprites[i].texture = this.textureB
        }

        this.sprites[i].x = this.blockWidth*i
        this.sprites[i].y = 0
      }
    }

    spritePointerDown(event){
      this.touched = true
      this.dragged = false
    }

    spritePointerMoved(event) {

      if (this.touched){
        console.log("pointermove")
        this.dragged = true
      }
    }

    spritePointerUp(event){
         this.touched = false
         console.log('this.draggeed',this.dragged)
     if (!this.dragged) {
        this.dragged = false
        this.active = !this.active
        this.alpha = 0.2
        this.texture = this.active ? this.parent.textureB : this.parent.textureA
        TweenLite.to(this,0.4,{alpha: 1})
       }
    }


   pointerDown(event) {
     app.stage.addChild(this)
      activated = this.id == ActiveIndex
      ActiveIndex = this.id
      ActiveRow = Rows[ActiveIndex]
      disableAllRowsExceptActive()
      drawWhiskers()
      placeButtons()
      pinA.sprite.x = this.width + LINE_START
      pinA.sprite.round()
      this.data = event.data
      this.startWidth = this.width
      this.dragStartY = event.data.global.y
      this.touching = true
      touching = true
      this.deltaTouch = {
        x: this.x - event.data.global.x,
        y: this.y - event.data.global.y
      }
    }

   pointerUp(event) {
      globalPointerUp()
      this.touching = false
      touching = false
    }

    pointerMove(event) {

      if (this.touching){
        const newPosition = this.data.getLocalPosition(this.parent);
        this.y = event.data.global.y + this.deltaTouch.y
      // Keep within number line bounds.
      if (this.y < TOP_LINE_Y){
        this.dragging = false
        this.y = TOP_LINE_Y
      } else if (this.y > BOTTOM_LINE_Y-BAR_HEIGHT) {
        this.dragging = false
        this.y = BOTTOM_LINE_Y  - BAR_HEIGHT
      }
        //this.x = event.data.global.x + this.deltaTouch.x
        //pinA.sprite.x = this.x + this.width
        this.dragged = true
      }
    }
  }



  function globalPointerUp(){
    // 
  }

  // Called on resize
  function resize(newFrame,flex){
    // Make sure all layout parameters are up to date.
    updateLayoutParams(newFrame)
  }

  function updateLayoutParams(newFrame){
    let frame;
    if (newFrame){
      frame = newFrame
    } else {
      frame = {width: WINDOW_WIDTH,height: WINDOW_HEIGHT}
    }
  }

  // Loading Script
  function load(){
    if (setup.props.features){
      features = setup.props.features
    }
  }

  // Functions attached to app: (need to be destroyed)
  app.resize = (frame) => resize(frame)
  app.resizable = true

  load()
};
