import React , {Component, Text,useEffect} from "react";
import { makeStyles, useTheme } from "@material-ui/core/styles";
import Arena from "./Arena";
import * as FractionWallScript from "../js/fractionwall.js";
import * as NumberLineToolScript from "../js/numberlinetool.js";
import * as GridToolScript from "../js/gridtool.js";
import * as OrderingToolScript from "../js/orderingtool.js";
import * as SharingToolScript from "../js/sharingtool.js";
import * as CuisenaireToolScript from "../js/cuisenairetool.js";
import * as CapacityTalkData from "../activitydata/CapacityTalk.json";
import { Grid , TextField} from "@material-ui/core";
import * as Pixi from "pixi.js";
import { TweenMax, TimelineLite, Power2, Elastic, CSSPlugin, TweenLite, TimelineMax } from "gsap/TweenMax";
import Button from "@material-ui/core/Button";
import Paper from "@material-ui/core/Paper";
import {Page,Document,pdfjs} from "react-pdf"

pdfjs.GlobalWorkerOptions.workerSrc = `//cdnjs.cloudflare.com/ajax/libs/pdf.js/${pdfjs.version}/pdf.worker.js`;


const useStyles = makeStyles(theme => ({
  container: {
    display: 'flex',
    flexWrap: 'wrap',
  },
  textField: {
    marginLeft: theme.spacing(1),
    marginRight: theme.spacing(1),
  },
  dense: {
    marginTop: theme.spacing(2),
  },
  menu: {
    width: 200,
  },
}));

export default class LessonPanel extends Component {
    constructor() {
        super();
      }

componentDidMount(){

   this.panel.appendChild(<Document file="pdf/pk07.pdf"> <Page pageNumber = {1} /> </Document>)

}

// So that the correct this.panel is highlighted on startup

  animate(k){
    var tl = new TimelineMax()
    if (k == -1) {
      tl.to(this.panel, 0.5, {x: this.panel.clientWidth,alpha: 0})
        .to(this.panel,0, {x: -this.panel.clientWidth,alpha: 1})
        .to(this.panel,1,{x: 0})

    } else if (k == 1) {
      tl.to(this.panel, 0.5, {x: -this.panel.clientWidth,alpha: 0})
        .to(this.panel,0, {x: this.panel.clientWidth,alpha: 1})
        .to(this.panel,1,{x: 0})
    }
  }

    initButtons(){
    let x = [ <a className = "problemButton center"  onClick = {()=>this.animate(-1)}>Previous</a>,<a className = "problemButton center"  onClick = {()=>this.animate(1)}>Next</a>]
    return x;
  }


 render() {

    return (
      <div className = "container center" >
      <div className = "problemTabs container">
            {this.initButtons()}
      </div>
        <div ref = {me => this.panel = me } > 
            <Document file="pdf/pk07.pdf"> <Page pageNumber = {1} /> </Document>
        </div>
      </div>
    );
 }
}
