import React , {Component, Text,useEffect} from "react";
import { makeStyles, useTheme } from "@material-ui/core/styles";
import Arena from "./Arena";
import * as FractionWallScript from "../js/fractionwall.js";
import * as NumberLineToolScript from "../js/numberlinetool.js";
import * as GridToolScript from "../js/gridtool.js";
import * as OrderingToolScript from "../js/orderingtool.js";
import * as SharingToolScript from "../js/sharingtool.js";
import * as CuisenaireToolScript from "../js/cuisenairetool.js";
import * as CapacityTalkData from "../activitydata/CapacityTalk.json";
import { Grid , TextField} from "@material-ui/core";
import * as Pixi from "pixi.js";
import { TweenMax, TimelineLite, Power2, Elastic, CSSPlugin, TweenLite, TimelineMax } from "gsap/TweenMax";
import Button from "@material-ui/core/Button";
import Paper from "@material-ui/core/Paper";
import {Page,Document} from "react-pdf"


const page1 = <Document file="pdf/pk07.pdf"> <Page pageNumber = {1} /> </Document>
const page100 = <Document file="pdf/pk07.pdf"> <Page pageNumber = {100} /> </Document>


const useStyles = makeStyles(theme => ({
  container: {
    display: 'flex',
    flexWrap: 'wrap',
  },
  textField: {
    marginLeft: theme.spacing(1),
    marginRight: theme.spacing(1),
  },
  dense: {
    marginTop: theme.spacing(2),
  },
  menu: {
    width: 200,
  },
}));

const scripts = [NumberLineToolScript,OrderingToolScript,FractionWallScript]


Pixi.settings.RESOLUTION = 3
var app = new Pixi.Application(0,0,{backgroundColor: 0xffffff,antialias: true});

export default function LessonPanel(props) {
  const classes = useStyles();
  const [panelNumber,setPanel] = React.useState(0)
  const [label,setLabel] = React.useState("Problem 1")
  const keys = [0,1,2]
  var buttonRefs = Array(10)
  var arena;
  var text;
  var n = 1

// So that the correct panel is highlighted on startup
useEffect(()=> {TweenLite.to(buttonRefs[panelNumber],0.3,{backgroundColor: 0xf24660,color: 0xffffff})})

  function animate(k){
    var tl = new TimelineMax()
    if (k < panelNumber) {
      tl.to(arena, 0.5, {x: arena.clientWidth,alpha: 0})
        .to(arena,0, {x: -arena.clientWidth,alpha: 1})
        .to(arena,1,{x: 0})
        TweenLite.to(buttonRefs[panelNumber],0.5,{backgroundColor: 0xffffff,color: 0x000000})
        TweenLite.to(buttonRefs[k],0.5,{backgroundColor: 0xf24660,color: 0xffffff})

    } else if (k > panelNumber) {
      TweenLite.to(buttonRefs[panelNumber],0.5,{backgroundColor: 0xffffff,color: 0x000000})
      TweenLite.to(buttonRefs[k],0.5,{backgroundColor: 0xf24660,color: 0xffffff})  
      tl.to(arena, 0.5, {x: -arena.clientWidth,alpha: 0})
        .to(arena,0, {x: arena.clientWidth,alpha: 1})
        .to(arena,1,{x: 0})
    }
    setPanel(k)
    n += 1 
    let i = Math.pow(-1,n) + 1

    TweenLite.to(text,0.5,{x: 500*i})
    //setTimeout(()=>text.label = "balls",1000)
    setLabel("Problem 2")
    console.log("test",text.label)
  }

  function initButtons(){
    let x = keys.map(k=> { 
      let button = (<a className = "problemButton center" ref = { b=> buttonRefs[k] = b } onClick = {()=>animate(k)}>{k+1}</a>)

      return button})
    return x;
  }

    return (
      <div >
      <div container className = "problemTabs container">
        {initButtons()}
      </div>
      <div ref = {me => arena = me } style = {{marginLeft: 20,marginRight: 20,marginBottom: 20}}> 
        <Grid container spacing  = {2}>
          <Grid item xs = {3}>
             <Paper className = "problemPrompt" style = {{flex: 1,flexDirection: "column",padding: 10}}>
               <p> {CapacityTalkData.default.problems[panelNumber]} </p>
                <TextField ref = {me=> {text = me}}
                  id="outlined"
                  label= {label}
                  defaultValue= "Answer"
                  className={classes.textField}
                  margin="normal"
                  variant="outlined"
                />
            </Paper>
            </Grid>
            <Grid item xs = {9}>
                {page1}
           </Grid>
          </Grid>
        </div>
      </div>
    );
}
