import React , {Component, Text,useEffect} from "react";
import { makeStyles, useTheme } from "@material-ui/core/styles";
import Arena from "./Arena";
import * as FractionWallScript from "../js/fractionwall.js";
import * as NumberLineToolScript from "../js/numberlinetool.js";
import * as GridToolScript from "../js/gridtool.js";
import * as OrderingToolScript from "../js/orderingtool.js";
import * as SharingToolScript from "../js/sharingtool.js";
import * as CuisenaireToolScript from "../js/cuisenairetool.js";
import * as CapacityTalkData from "../activitydata/CapacityTalk.json";
import { Grid , TextField} from "@material-ui/core";
import * as Pixi from "pixi.js";
import { TweenMax, TimelineLite, Power2, Elastic, CSSPlugin, TweenLite, TimelineMax } from "gsap/TweenMax";
import Button from "@material-ui/core/Button";
import Paper from "@material-ui/core/Paper";
import {Page,Document,pdfjs} from "react-pdf"

pdfjs.GlobalWorkerOptions.workerSrc = `//cdnjs.cloudflare.com/ajax/libs/pdf.js/${pdfjs.version}/pdf.worker.js`;


const useStyles = makeStyles(theme => ({
  container: {
    alignItems: 'center',
    flexDirection: 'row'
  },
  textField: {
    marginLeft: theme.spacing(1),
    marginRight: theme.spacing(1),
  },
  dense: {
    marginTop: theme.spacing(2),
  },
  menu: {
    width: 200,
  },
}));

export default function LessonPanel(props) {
  const classes = useStyles();
  const [panelNumber,setPanel] = React.useState(1)
  const [label,setLabel] = React.useState("Problem 1")
  const keys = [0,1,2]
  var panel;
  let page1;
  let width;
  const page100 = <Document file="pdf/pk07.pdf"> <Page pageNumber = {1} /> </Document>


// So that the correct panel is highlighted on startup
useEffect(()=> {
    console.log("panel",panel.clientWidth)
    width = panel.clientWidth
    page1 = <Document file="pdf/pk07.pdf"> <Page width = {200}  pageNumber = {1} /> </Document>
})
  function animate(k){
    var tl = new TimelineMax()
    if (k == -1) {
      tl.to(panel, 0.5, {x: panel.clientWidth,alpha: 0})
        .to(panel,0, {x: -panel.clientWidth,alpha: 1})
        .to(panel,1,{x: 0})

    } else if (k == 1) {
      tl.to(panel, 0.5, {x: -panel.clientWidth,alpha: 0})
        .to(panel,0, {x: panel.clientWidth,alpha: 1})
        .to(panel,1,{x: 0})
    }
    setTimeout(()=>setPanel(panelNumber+1),500)
  }

  function initButtons(){
    let x = [ <Button variant="outlined" size="small" color="primary" onClick = {()=>animate(-1)}>Previous</Button>,<Button variant="outlined" size="small" color="primary" onClick = {()=>animate(1)}>Next</Button>]
    return x;
  }


    return (
      <div centered>
      <div center>
        <Button variant="outlined" size="small" color="primary" onClick = {()=>animate(-1)}>Previous</Button>
      </div>
        <div ref = {me => panel = me } > 
            <Document file="pdf/MakingNumbersActivity.pdf"> <Page width = {window.innerWidth}  pageNumber = {panelNumber} /> </Document>
        </div>
      </div>
    );
}
