  const wallX = (windowWidth - wallWidth) / 2;
  const height = setup.height / 15;
  let dx = setup.height / 15;
  let dy = dx;
  let dragged = false
  let dragStartedAt;

  let backGround = new PIXI.Sprite.from(blueGradient);

  backGround.width = setup.width
  backGround.height = setup.height
  backGround.x = 0;
  backGround.y = 0;

