        window.createjs.Tween.get(t).to(
          { x: (wallWidth / den) * i },
          1000,
          window.createjs.Ease.getPowInOut(4)
        );
      }
    });
  }

  function swapRow(row, start, end) {
    if (Math.abs(start - end) < 30) {
      row.forEach(t =>
        window.createjs.Tween.get(t)
          .to({ y: start }, 300, window.createjs.Ease.getPowInOut(4))
          .call(swapCompleted)
      );
