        if (wallObj[r].y > start && wallObj[r].y < end) {
          moveRowUp(wallObj[r]);
        }
      }
      let roundedEnd = Math.floor(end / height) * height;
      row.forEach(t =>
        window.createjs.Tween.get(t)
          .to({ y: roundedEnd }, 300, window.createjs.Ease.getPowInOut(4))
          .call(swapCompleted)
      );
