    tile.interactive = true;
    tile.denText = den;
    tile.denValue = d
    tile.buttonMode = true;

    tile
      .on("pointerdown", onDragStart)
      .on("pointerup", onDragEnd)
      .on("pointermove", onDragMove);

    // move the sprite to its designated position
    tile.x = x;
    tile.y = y;

    return tile;
  }


  function onContainerDragStart(event) {
    console.log("container clicked!")
    if (!swapping) {
      // console.log("setting dragging to true");
      this.dragging = true;
      dragStartedAt = this.y;
      this.data = event.data;
      this.alpha = 0.5;
    }
  }

  function onContainerDragEnd() {
    if (this.dragged) {
      this.dragged = false;
      let row = wallObj[this.denValue];
      activeRow = this.den;
      swapRow(row, dragStartedAt, this.y);
      if (this.y > 12 * height) {
        animateTicks(activeRow);
      }
    } else {

      this.active = !this.active;
      this.denText.alpha = this.active ? 1 : 0
      this.texture = this.active
        ? newTexture(this, 0xff708a)
        : newTexture(this);

    }
    this.alpha = 1;
    this.dragging = false;
    this.data = null;
  }

  function onContainerDragMove() {
    console.log("dragging",this.dragging)
    if (this.dragging) {
      dragged = true;
      var newPosition = this.data.getLocalPosition(this.parent);
      wallObj[this.denValue].y = newPosition
    }
  }


  function onDragStart(event) {
    if (!swapping) {
      // console.log("setting dragging to true");
      this.dragging = true;
      dragStartedAt = this.y;
      this.data = event.data;
      this.alpha = 0.5;
    }
  }

