import * as PIXI from "pixi.js";
import * as CONST from "./const.js";
import { TweenMax, TimelineLite, Power2, Elastic, CSSPlugin, TweenLite, TimelineMax } from "gsap/TweenMax";
const ASSETS = CONST.ASSETS



export class Fraction extends PIXI.Container {
  constructor(n,d,w){
    super()
    this.numerator = n+""
    this.denominator = d+""
    this.numDigits = this.numerator.length
    this.denDigits = this.denominator.length 
    this.maxDigits = Math.max(this.numDigits,this.denDigits)
    this.fontSize = w/(this.maxDigits)
    this.compression = 0.6

    if (this.maxDigits == 3){
      this.compression = 0.70
      this.lineCompression = 70
    } else if (this.maxDigits == 2){
      this.compression = 0.66
    }

    // Numerator
    this.N = new PIXI.Text()
    this.N.anchor.x = 0.5
    //this.N.height = this.fontSize*1.7
    this.N.width = w/this.maxDigits*this.numDigits*this.compression
    this.N.x = w/2
    this.N.y = 0
    this.N.text = n
    this.N.style.fontSize = this.fontSize
    this.addChild(this.N)

    // Denominator
    this.D = new PIXI.Text()
    this.D.anchor.x = 0.5
    //this.D.height = this.fontSize*1.7
    this.D.width = w/this.maxDigits*this.denDigits*this.compression
    this.D.x = w/2
    this.D.y = this.height
    this.D.text = d
    this.D.style.fontSize = this.fontSize
    this.addChild(this.D)

    // Mid Line
    this.L = new PIXI.Graphics()
    this.L.lineStyle(w/20,0x000000)
    this.L.lineTo(w,0)
    this.L.y = this.height/2
    this.addChild(this.L)

  }

  set(n,d){
    this.numerator = n 
    this.denominator = d
    this.N.text = n
    this.D.text = d
  }
}
